def get_transaction_name(code):
    for k, v in bytecodes.items():
        if code == v:
            return k+"_transaction"
    return "unknown transaction"



def get_code_by_filename(script_file):
    with open(script_file) as f:
        amap = json.load(f)
        return amap['code']