"""
Basic Sample Python to define common mathematical functions
"""
import math

def calculate_square(num):
    """
    Returns the square of a given number
    """
    return num * num


def add(a, b):
    """
    Returns the sum of two numbers
    """
    return a + b


def calculate_sin(x):
    """
    Return the sine of x (measured in radians).
    """
    return math.sin(x)
